        tag_entity = Tag.find_or_create_by_value(tag)
        # Then connect it to a new AbstractTag associated with this Abstract
        abstract_tag_temp = AbstractTag.find_or_create_by_abstract_id_and_tag_id(self.id, tag_entity.id)
      end
    end
    # Remove the tags that aren't in there anymore
    self.tags.each do |tag|
      AbstractTag.find_by_abstract_id_and_tag_id(self.id, tag.id).delete if !tags.include?(tag.value)
    end
