  def refresh_connections
    create_tags
    if self.user_id.nil?
      return
    else
      usr = User.find(self.user_id)
      usr.attendances.all.each do |atnd|
        conf = Conference.find(atnd.conference_id)
        Connection.refresh_conf_connections(conf, usr.id)
      end
    end
  end
  
